import React, {Component} from 'react';

class putGrade extends Component {

	state = {playerNum:"", inning:"", score:""}

	onChangeValue = (e) => {
		this.setState({
			[e.target.name] : e.target.value
		});
	}

	render (){
		return (

			<div className ="inputGrade">
				<input
					placeholder = "선수번호 입력"
					name = "playerNum"
					value = {this.state.playerNum}
					onChange = {this.onChangeValue}
				/>
				<input
					placeholder = "이닝수 입력"
					name = "inning"
					value = {this.state.inning}
					onChange = {this.onChangeValue}
				/>
				<input
					placeholder = "자책점 입력"
					name = "score"
					value = {this.state.score}
					onChange = {this.onChangeValue}
				/>
				<button
					className = "putGrade">

				</button>


			</div>
		);
	}


}


export default putGrade;
